package ArrayEven;

public class Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] IntArray= new int[10];
		int i=1;
		System.out.println("Even numbers from 1 to 10 are:");
		
		for(i=1;i<IntArray.length;i++)
		 { 
			 if(i%2==0)
			 {
 			   System.out.println(i);
			 }
		 
			 }
				 
		

	} 

}
